import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

const AddToDo = ({ setTodo }) => {
  const [todoItem, setTodoItem] = useState('');

  const handleAdd = () => {
    const newTodo = {
      id: uuidv4(),
      todo: todoItem,
      isCompleted: false,
    };
    setTodo((state) => {
      const a = [...state, newTodo];
      localStorage.setItem('todos', JSON.stringify(a));
      return a;
    });
  };

  return (
    <>
