              }}
              placeholder="Add to-do item"
            ></Input>
          </Form.Item>
          <Form.Item>
            <Button type="primary" onClick={handleAdd}>
              Add
            </Button>
          </Form.Item>
        </Form>
      </Row> */}

      <form>
        <input
          onChange={(event) => {
            setTodoItem(event.target.value);
          }}
          placeholder="To-do item"
        ></input>
        <button onClick={handleAdd}>Add</button>
      </form>
    </>
  );
};

export default AddToDo;
