import React, { useState } from 'react';
import styled from 'styled-components';
import CreateToDo from './create-to-do';
import { observer } from 'mobx-react';
import { useStores } from '../hooks/use-store';

const Title = styled.h1`
  font-size: 1.5em;
  font-family: 'Opens Sans';
`;

const Wrapper = styled.section`
  text-align: center;
`;

const BtnWrapper = styled.div`
  padding: 0 15px;
`;

const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 0.5em;
  padding: 0.25em 1em;
`;

const TaskContainer = styled.div`
  display: inline-grid;
`;

const Task = observer(({ todo }) => {
  const { todosStore } = useStores();
  return (
    <>
      <TaskContainer>
        <div style={{ textDecoration: todo.completed ? 'line-through' : '' }}>
          {todo.title}
        </div>
        <BtnWrapper>
          <Button onClick={() => todosStore.completeToDo(todo.id)}>Done</Button>
          <Button onClick={() => todosStore.removeToDo(todo.id)}>x</Button>
        </BtnWrapper>
      </TaskContainer>
    </>
  );
});

const Todo = () => {
  const { todosStore } = useStores();

  return (
    <Wrapper>
      <Title>TODO - ITEMS</Title>
      {todosStore.todos.map((todo) => (
        <Task todo={todo} />
      ))}
      <CreateToDo />
    </Wrapper>
  );
};

export default observer(Todo);
