      this.value = await asyncFunction();
      return this; // when done
    })();
  }

  @action
  async getDetails() {
    const response = await axios.get(
      `https://fakerapi.it/api/v1/credit_cards?_quantity=1`,
    );
  }
}
class CCStore {
