import styled from 'styled-components';
import { useStores } from '../hooks/cc-use-store';
import { observer } from 'mobx-react';

const Container = styled.div`
  position: fixed;
  top: 30%;
  left: 25%;
`;
const Card = styled.div`
  color: white;
  width: 100vh;
  height: 40vh;
  background: black;
  text-align: center;
`;
const Text = styled.span`
  font-family: monospace;
`;
const Type = styled.div`
  padding: 5px;
`;
const Number = styled.div`
  font-size: xxx-large;
  padding: 5px;
`;
const Expiration = styled.div`
  padding: 5px;
`;
const Owner = styled.div`
  padding: 5px;
`;

const Generate = styled.button`
  background: transparent;
  border: 2px solid black;
  margin: 30px 0;
  padding: 0.25em 1em;
`;

const CCMaker = observer(({ ccs }) => {
  const { ccStore } = useStores();
  return (
    <Container>
      <Card>
        <Number>
          <Text>{ccStore.ccs.number}</Text>
          <Text>{ccStore.number}</Text>
        </Number>
        <Expiration>
          <Text>{ccStore.ccs.expiration}</Text>
        </Expiration>
        <Owner>
          <Text>{ccStore.ccs.owner}</Text>
        </Owner>
        <Type>
          <Text>{ccStore.ccs.type}</Text>
        </Type>
      </Card>
      <Generate onClick={() => ccStore.getDetails()}>Generate</Generate>
    </Container>
  );
});

export default CCMaker;
