import React, { useState } from 'react';
import styled from 'styled-components';
import { v4 as uuidv4 } from 'uuid';
import CreateToDo from './create-to-do';

const Title = styled.h1`
  font-size: 1.5em;
  font-family: 'Opens Sans';
`;

const Wrapper = styled.section`
  text-align: center;
  background: papayawhip;
`;

const DivWrapper = styled.div`
  padding: 15px;
  display: flex;
`;

const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`;

const Task = ({ task, index, completeToDo, removeToDo }) => {
  return (
    <Wrapper
      className="task"
      style={{ textDecoration: task.completed ? 'line-through' : '' }}
    >
      <DivWrapper>{task.title}</DivWrapper>
      <DivWrapper>
        <Button onClick={() => completeToDo(index)}>Complete</Button>
        <Button onClick={() => removeToDo(task.id)}>x</Button>
      </DivWrapper>
    </Wrapper>
  );
};

const Todo = () => {
  const [tasks, setTasks] = useState([]);

  const addToDo = (title) => {
    const newTasks = [...tasks, { title, completed: false, id: uuidv4() }];
    setTasks(newTasks);
  };

  const completeToDo = (index) => {
    const newTasks = [...tasks];
    newTasks[index].completed = true;
    setTasks(newTasks);
  };

  const removeToDo = (id) => {
    const newTasks = [...tasks];
    const remainder = newTasks.filter((task) => task.id !== id);
    setTasks(remainder);
  };

  return (
    <Wrapper>
      <Title>TODO - ITEMS</Title>
      <div className="tasks">
        {tasks.map((task, index) => (
          <Task
            task={task}
            index={index}
            key={index}
            completeToDo={completeToDo}
            removeToDo={removeToDo}
          />
        ))}
      </div>
      <CreateToDo addToDo={addToDo} />
    </Wrapper>
  );
};

export default Todo;
