import { observable, action } from 'mobx';
import axios from 'axios';

class CCsStore {
  @observable ccs = {};
  /* @observable type = '';
  @observable owner = '';
  @observable number = '';
  @observable expiration = ''; */

  @action
  getDetails() {
    axios
      .get(`https://fakerapi.it/api/v1/credit_cards?_quantity=1`)
      .then(function (response) {
        console.log(response.data.data[0]);
        console.log(
          response.data.data[0].type,
          response.data.data[0].owner,
          response.data.data[0].number,
          response.data.data[0].expiration,
        );
        this.ccs = new CCStore({
          type: response.data.data[0].type,
          number: response.data.data[0].number,
          owner: response.data.data[0].owner,
          expiration: response.data.data[0].expiration,
        });
        /* this.type = response.data.data[0].type;
        this.owner = response.data.data[0].owner;
        this.number = response.data.data[0].number;
        this.expiration = response.data.data[0].expiration; */

        // console.log(this.type, this.owner, this.number, this.expiration);
        console.log(
          this.ccs.type,
          this.ccs.number,
          this.ccs.owner,
          this.ccs.expiration,
        );
      })
      .catch(function (error) {
        console.log(error);
      });
  }
}

class CCStore {
  @observable type = '';
  @observable owner = '';
  @observable number = '';
  @observable expiration = '';

  constructor(props) {
    /* this.type = type;
    this.number = number;
    this.owner = owner;
    this.expiration = expiration; */
    console.log(props);
    object.assign(this, props);
  }
}

export default CCsStore;
